options.customCss:
/* --- ROOT VARIABLES (Dark Professional Theme) --- */
:root {
	/* --- FONT MAPPING --- */
	--font-family: 'Inter', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-size-body: 14px;
	--font-size-label: 14px;
	--font-size-input: 14px;
	--font-size-header: 24px;
	--line-height-base: 1.6;

	/* --- COLOR MAPPING (Dark Slate/Purple Theme) --- */
	--color-page-bg: #111827; /* Dark Slate */
	--color-card-bg-glass: rgba(29, 39, 53, 0.65); /* Darker, less transparent glass */
	--color-card-border-glass: rgba(255, 255, 255, 0.1); /* Subtle light border */
	--color-input-bg: #1F2937; /* Slightly lighter than page bg */
	--color-header: #F9FAFB; /* Off-white for high contrast */
	--color-label: #9CA3AF; /* Light gray for secondary text */
	--color-input-text: #F9FAFB;
	--color-input-border: #4B5563; /* Mid-gray border */
	--color-focus-border: #8B5CF6; /* Vibrant Purple */
	--color-link: #8B5CF6;
	--color-submit-btn-bg: #8B5CF6;
	--color-submit-btn-hover-bg: #7C3AED; /* Darker Purple */
	--color-submit-btn-text: #FFFFFF;
	--color-error: #F43F5E; /* Vibrant Rose for errors */
	--color-required: #F43F5E;

	/* --- DIMENSIONS & SPACING --- */
	--container-width: 550px;
	--border-radius-card: 16px;
	--border-radius-input: 8px;
	--padding-card: 32px;
	--padding-form-input: 14px;
	--submit-btn-height: 48px;
	--checkbox-size: 20px;

	/* --- EFFECTS & ANIMATIONS --- */
	--box-shadow-card: 0 16px 50px rgba(0, 0, 0, 0.3); /* Deeper shadow for dark mode */
	--transition-speed: 0.25s;
	--animation-speed-fast: 0.2s;
	--animation-speed-normal: 0.4s;
}

/* === KEYFRAME ANIMATIONS === */
@keyframes fadeInSlideUp {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes backgroundPan {
	0% { background-position: 0% 50%; }
	50% { background-position: 100% 50%; }
	100% { background-position: 0% 50%; }
}

/* === GLOBAL LAYOUT & STYLES === */
body {
    /* Dynamic animated aurora gradient background */
    background-color: var(--color-page-bg);
    background-image:
        radial-gradient(at 20% 100%, hsla(278, 60%, 45%, 0.2) 0px, transparent 50%),
        radial-gradient(at 80% 0%, hsla(215, 60%, 40%, 0.2) 0px, transparent 50%),
        radial-gradient(at 50% 50%, hsla(259, 70%, 55%, 0.1) 0px, transparent 50%);
    background-size: 200% 200%;
    animation: backgroundPan 15s ease-in-out infinite;

    line-height: var(--line-height-base);
    font-size: var(--font-size-body);
    color: var(--color-label); /* Set default text color for body */

    /* Flexbox properties for true centering */
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    margin: 0;
    overflow-x: hidden; /* Prevent horizontal scroll from large gradients */
}

.form-container {
    padding: 2rem 1rem;
}

/* === THE GLASSMORPHISM CARD === */
.card {
    background: var(--color-card-bg-glass);
    backdrop-filter: blur(16px) saturate(150%);
    -webkit-backdrop-filter: blur(16px) saturate(150%);
    border-radius: var(--border-radius-card);
    border: 1px solid var(--color-card-border-glass);
    box-shadow: var(--box-shadow-card);
    transition: box-shadow var(--animation-speed-normal) ease;

    /* Card Load-in Animation */
    animation: fadeInSlideUp 0.6s ease-out forwards;
}
.card:hover {
    box-shadow: 0 0 50px rgba(139, 92, 246, 0.15); /* Subtle purple glow on hover */
}

/* === FORM ELEMENT STYLING === */
.form-input {
    border-radius: var(--border-radius-input);
    transition: border-color var(--transition-speed) ease, box-shadow var(--transition-speed) ease;
    background-color: var(--color-input-bg);
    border: 1px solid var(--color-input-border);
    color: var(--color-input-text);
}
.form-input::placeholder {
    color: var(--color-label);
    opacity: 0.7;
}
.form-input:focus {
    /* Glow effect on focus */
    box-shadow: 0 0 0 3px rgba(139, 92, 246, 0.25);
    border-color: var(--color-focus-border);
    outline: none; /* Remove default outline */
}

textarea.form-input {
    min-height: 150px;
    resize: vertical;
}

/* Submit Button */
button.submit-btn {
    transition: all var(--transition-speed) ease-in-out;
    font-family: var(--font-family);
    font-weight: var(--font-weight-bold);
    color: var(--color-submit-btn-text);
    background-color: var(--color-submit-btn-bg);
    border-radius: var(--border-radius-input);
    border: none;
    cursor: pointer;
}
button.submit-btn:hover {
    background-color: var(--color-submit-btn-hover-bg);
    /* Add a lift and glow effect on hover */
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(139, 92, 246, 0.3);
}

/* === MODERN CHECKBOX STYLING === */
label.multiselect-label {
    display: flex;
    align-items: center;
    cursor: pointer;
    position: relative;
    padding: 8px 0;
}
label.multiselect-label span {
    padding-left: 12px;
    color: var(--color-label);
    transition: color var(--transition-speed) ease;
}
label.multiselect-label:hover span {
    color: var(--color-input-text); /* Lighten label text on hover */
}
label.multiselect-label input[type="checkbox"] {
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
}
label.multiselect-label span::before {
    content: '';
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    width: var(--checkbox-size);
    height: var(--checkbox-size);
    background-color: var(--color-input-bg);
    border: 1px solid var(--color-input-border);
    border-radius: 6px;
    transition: all var(--animation-speed-fast) ease-in-out;
}
label.multiselect-label input[type="checkbox"]:checked + span::before {
    background-color: var(--color-focus-border);
    border-color: var(--color-focus-border);
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 16 16' fill='white'%3e%3cpath d='M12.207 4.793a1 1 0 010 1.414l-5 5a1 1 0 01-1.414 0l-2-2a1 1 0 011.414-1.414L6.5 9.086l4.293-4.293a1 1 0 011.414 0z'/%3e%3c/svg%3e");
    background-position: center;
    background-repeat: no-repeat;
    /* Animation for the checkmark appearing */
    transform: translateY(-50%) scale(1.05);
}
label.multiselect-label input[type="checkbox"]:focus-visible + span::before {
    /* Use a glow for focus instead of a harsh outline */
    box-shadow: 0 0 0 3px rgba(139, 92, 246, 0.3);
}

