parameters.jsCode:
//Get the raw output from the AI Agent node
const rawOutput = $json.output || "";

//Remove ```json and``` markers if present
let cleaned = rawOutput.replace(/```json|```/g,'').trim();

//parse json if its valid
let post = "";
try {
  const parsed = JSON.parse(cleaned);
  post = parsed.post || "";
} catch (e){
  post = cleaned; //fallback if json.parse fails
}

//Return a clean object
return [{ json: {post} }];

// Get the input data correctly
const rawText = $input.first().json.text || $input.first().json.output || "";
// Romove code block markers
let cleaned = rawText.replace(/```json|```/g, '').trim();

let finalText = "";
try {
  //Parse the JSON if valid
  const parsed = JSON.parse(cleaned);
  finalText = parsed.headline || "";
} catch (e) {
  // If parsing fails, try to clean manually
  finalText = cleaned.replace(/^{|}$/g, '')
                     .replace(/"headline":/i, '')
                     .replace(/[*{}]/g, '')
                     .trim();
  }
// Replace only the raw string
return [{ json: {text: finalText} }];
